document.querySelectorAll("#titleScreen button").forEach((element)=>{
    element.addEventListener("mouseover", (event) => {
        const clone = hover.cloneNode(true);
        clone.play();
    });

    element.addEventListener("mousedown", (event) => {
        const clone = click.cloneNode(true);
        clone.play();
        document.getElementById('titleScreen').style.display = "none"
        document.getElementById('menuScreen').style.display = "inherit"
        loadMenu(element.innerText)
    });
})

document.getElementById('back').addEventListener("mousedown", (event) => {
    const clone = back.cloneNode(true);
    clone.play();
    document.getElementById('titleScreen').style.display = "inherit"
    document.getElementById('menuScreen').style.display = "none"
});

